package com.syntax.class03;

public class ifElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double budget = 100000;
		double carPrice = 12000;

//using if 
		if (budget > carPrice) {
			System.out.println("I will buy this car today");
			System.out.println(" and will be happy");
		} else {
			System.out.println("I will save up for buying new car ");
			System.out.println("and will buy this car next year");
		}

	}

}
